def apply_happiness(sad_file: str, happy_output_file: str):
    with open(sad_file, 'r') as fd:
        sad_content = fd.read()

    for sad_line, happy_line in zip(SAD_LINES, HAPPY_LINES):
        sad_content = sad_content.replace(sad_line, happy_line)
    # No more sad :)
    happy_content = sad_content
    with open(happy_output_file, 'w') as fd:
        fd.write(happy_content)


if __name__ == '__main__':
    parser = ArgumentParser(description='Apply Happyness!')
    parser.add_argument('-i', '--input-file', required=True, help='Sad file')
    parser.add_argument('-o', '--output-file', required=True, help='Happy output file')

    args = parser.parse_args()

    apply_happiness(args.input_file, args.output_file)
